import React, { useState } from 'react';
import { useDropzone } from 'react-dropzone';

function App() {
  const [files, setFiles] = useState([]);
  const { getRootProps, getInputProps } = useDropzone({
    onDrop: (acceptedFiles) => setFiles(acceptedFiles),
  });

  const handleUpload = () => {
    // You would handle the Google Drive upload here
    console.log('Uploading files to Google Drive...');
  };

  return (
    <div className="flex items-center justify-center min-h-screen bg-gray-100">
      <div className="bg-white p-10 rounded-lg shadow-lg text-center w-96">
        {/* Company Logo */}
        <img src="/imgs/grovery-logo-update-color_tall.svg" alt="Company Logo" className="mx-auto mb-5 w-32" />

        <h1 className="text-xl font-bold mb-5">Upload Your File</h1>

        {/* Drag and Drop Area */}
        <div
          {...getRootProps()}
          className="border-2 border-dashed border-gray-400 p-5 rounded-lg cursor-pointer mb-5"
        >
          <input {...getInputProps()} />
          <p>Drag & drop files here, or click to select files</p>
        </div>

        {/* File List */}
        {files.length > 0 && (
          <ul className="mb-5">
            {files.map((file) => (
              <li key={file.name}>{file.name}</li>
            ))}
          </ul>
        )}

        {/* Upload Button */}
        <button
          onClick={handleUpload}
          className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
        >
          Upload
        </button>
      </div>
    </div>
  );
}

export default App;
