import React, { useState } from 'react';
import { useDropzone } from 'react-dropzone';
import axios from 'axios';

function App() {
  const [files, setFiles] = useState([]);
  const [progress, setProgress] = useState(0);
  const [statusMessage, setStatusMessage] = useState('');
  const [isSuccess, setIsSuccess] = useState(false); // To track success or error status

  const { getRootProps, getInputProps } = useDropzone({
    onDrop: (acceptedFiles) => setFiles(acceptedFiles),
  });

  const handleUpload = async () => {
    if (files.length === 0) {
      setStatusMessage('No files selected for upload.');
      setIsSuccess(false);
      return;
    }

    const formData = new FormData();
    formData.append('file', files[0]);

    try {
      // Reset progress and status
      setProgress(0);
      setStatusMessage('Uploading...');
      setIsSuccess(false);

      const response = await axios.post('http://localhost:5001/upload', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
        onUploadProgress: (progressEvent) => {
          const percentCompleted = Math.round(
            (progressEvent.loaded * 100) / progressEvent.total
          );
          setProgress(percentCompleted);
        },
      });

      // On success
      setStatusMessage(`File uploaded successfully to S3! S3 URL: ${response.data.s3Url}`);
      setIsSuccess(true);
      setProgress(100);
    } catch (error) {
      console.error('Error uploading file:', error);
      setStatusMessage('Error uploading file to S3.');
      setIsSuccess(false);
      setProgress(0);
    }
  };

  return (
    <div className="flex items-center justify-center min-h-screen bg-gray-100">
      <div className="bg-white p-10 rounded-lg shadow-lg text-center w-96">
        <h1 className="text-xl font-bold mb-5">Upload Your File</h1>

        {/* Drag and Drop Area */}
        <div
          {...getRootProps()}
          className="border-2 border-dashed border-gray-400 p-5 rounded-lg cursor-pointer mb-5"
        >
          <input {...getInputProps()} />
          <p>Drag & drop files here, or click to select files</p>
        </div>

        {/* Selected Files */}
        {files.length > 0 && (
          <ul className="mb-5">
            {files.map((file) => (
              <li key={file.name}>{file.name}</li>
            ))}
          </ul>
        )}

        {/* Progress Bar */}
        {progress > 0 && (
          <div className="w-full bg-gray-200 rounded-full h-4 mb-5">
            <div
              className="bg-blue-600 h-4 rounded-full"
              style={{ width: `${progress}%` }}
            ></div>
          </div>
        )}

        {/* Upload Button */}
        <button
          onClick={handleUpload}
          className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
        >
          Upload
        </button>

        {/* Status Message */}
        {statusMessage && (
          <div className='overflow-auto'>
          <p
            className={`mt-4 ${
              isSuccess ? 'text-green-500' : 'text-red-500'
            }`}
          >
            {statusMessage}
          </p>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
